
export{DataSources}
import{List}from'./list/list.min.js'
import{Form}from'./form/form.min.js'
class DataSources{constructor(ctr){this.list=new List(ctr);this.form=new Form(ctr);}
draw(sp,{ds}){if(!sp&&!ds){return this.list.draw();}
if(sp&&!ds&&sp.has('action')&&sp.get('action')==='new'){return this.form.draw();}
if(sp&&ds&&sp.has('action')&&sp.get('action')==='edit'){return this.form.drawEdit(ds);}}}