
export{Details}
class Details{constructor(ctr){this.container=ctr;}
draw(ds){fetch(active_cmp.constructor.mr_api+"/"+ds).then((response)=>{if(response.ok){return response.json()}
throw'Error retrieving data-source - '+response.status;}).then((data)=>{fetch('/components/data-sources/details/template.html').then((response)=>response.text()).then((template)=>{this.container.innerHTML=Mustache.render(template,{id:ds,name:data['name'],type_id:data['type_id'],pipeline_id:data['pipeline_id']});});}).catch((err)=>{this.container.innerHTML=err});}}