
export{List}
class List{static api='/api/machine-registry/machines';constructor(ctr){this.container=ctr;}
draw(){fetch(active_cmp.constructor.mr_api).then((response)=>{if(response.ok){return response.json()}
throw'Error retrieving data-sources - '+response.status;}).then((data)=>{fetch('/components/data-sources/list/template.html').then((response)=>response.text()).then((template)=>{let items=[];for(let[key,value]of Object.entries(data)){value=JSON.parse(value);items.push({id:key,name:value['name'],type_id:value['platform_type_id'],p_id:value['platform_id'],pipeline_id:value['pipeline_id']})}
this.container.innerHTML=Mustache.render(template,{ds_items:items});});}).catch((err)=>{this.container.innerHTML=err});}
delete(id){if(confirm('Delete '+id+'?')){fetch(active_cmp.constructor.mr_api+'/'+id,{method:'DELETE'}).then((response)=>{if(response.ok){window.open('/data-sources','_self');}else{throw response.status;}}).catch((err)=>{alert('Error deleting '+id+' - '+err);})}}}