
export{Jobs}
import{List}from'./list/list.min.js'
import{Details}from'./details/details.min.js'
class Jobs{static jm_api='/api/job-manager';constructor(ctr){this.loadStyle();this.list=new List(ctr);this.details=new Details(ctr);}
loadStyle(){let element=document.createElement("link");element.href="/components/jobs/style.css";element.rel="stylesheet";document.getElementsByTagName("head")[0].appendChild(element);}
draw(sp,{job}){if(!sp&&!job){return this.list.draw(this.constructor.jm_api+'/jobs',false);}
if(sp&&!job&&sp.has('show')&&sp.get('show')==='history'){return this.list.draw(this.constructor.jm_api+'/history',true);}
if(!sp&&job){return this.details.draw(this.constructor.jm_api+'/jobs',job)}
if(sp&&job&&sp.has('show')&&sp.get('show')==='history'){return this.details.draw(this.constructor.jm_api+'/history',job)}}}