
export{Form}
class Form{static stage_template;constructor(ctr){this.container=ctr;this.stage_container=null;this.workers={};this.st_count=0;this.stages=[];}
async getTemplates(){let response=await fetch('/components/pipelines/form/stage-template.html')
Form.stage_template=await response.text();return await fetch('/components/pipelines/form/template.html');}
draw(pl_data=null){fetch(active_cmp.constructor.w_api).then((response)=>{if(response.ok){return response.json()}
throw'Error retrieving Workers - '+response.status;}).then((data)=>{this.getTemplates().then((response)=>response.text()).then((template)=>{let workers=[];for(let[key,value]of Object.entries(data)){value=JSON.parse(value);workers.push({w_id:key,w_name:value['name']})
this.workers[key]=value;}
if(pl_data){this.container.innerHTML=Mustache.render(template,{workers:workers,id:pl_data['id'],name:pl_data['name'],stages:pl_data['stages']});}else{this.container.innerHTML=Mustache.render(template,{workers:workers});}
let form=this.container.getElementsByTagName('form')[0];form.addEventListener('submit',this.submit);this.stage_container=document.getElementById('stages');});}).catch((err)=>{this.container.innerHTML=err;});}
drawEdit(pl){fetch(active_cmp.constructor.api+'/'+pl).then((response)=>{if(response.ok){return response.json()}
throw'Error retrieving Pipeline - '+response.status;}).then((data)=>{data['id']=pl;this.draw(data);}).catch((err)=>{this.container.innerHTML=err;});}
addStage(wk_id){console.log(wk_id);console.log(this.st_count);console.log(this.workers[wk_id]);let configs=[];if(this.workers[wk_id]['configs']){for(let[key,value]of Object.entries(this.workers[wk_id]['configs'])){configs.push({key:key,value:value})}}
this.stage_container.append(document.createRange().createContextualFragment(Mustache.render(Form.stage_template,{num:this.st_count,w_name:this.workers[wk_id]['name'],w_id:wk_id,input:this.workers[wk_id]['input']?this.workers[wk_id]['input']['fields']:null,output:(this.workers[wk_id]['output'])?this.workers[wk_id]['output']['fields']:null,has_configs:!!(this.workers[wk_id]['configs']),configs:configs})));this.st_count++;}
removeStage(id){let element=document.getElementById(id);let parent=element.parentElement;parent.removeChild(element);}}