
export{Details}
class Details{constructor(ctr){this.container=ctr;}
genConfigs(items){let configs=[];if(items){for(const[key,value]of Object.entries(items)){configs.push({conf_key:key,conf_value:(value)?value:'null'})}}
return configs;}
draw(pa_id){fetch(active_cmp.constructor.api+'/'+pa_id).then((response)=>{if(response.ok){return response.json()}
throw'Error retrieving Protocol-Adapter - '+response.status;}).then((data)=>{fetch('/components/protocol-adapters/registry/details/template.html').then((response)=>response.text()).then((template)=>{this.container.innerHTML=Mustache.render(template,{id:pa_id,name:data['name'],image:data['image'],description:data['description'],data_cache_path:data['data_cache_path'],conf_section:!!(data['configs']),configs:this.genConfigs(data['configs']),port_section:!!(data['ports']),ports:data['ports']});});}).catch((err)=>{this.container.innerHTML=err});}}