
export{List}
class List{constructor(ctr){this.container=ctr;}
draw(){fetch(active_cmp.constructor.api).then((response)=>{if(response.ok){return response.json()}
throw'Error retrieving images - '+response.status;}).then((data)=>{fetch('/components/system/images/list/template.html').then((response)=>response.text()).then((template)=>{let items=[];for(let[key,value]of Object.entries(data)){items.push({name:key,hash:value['hash'],created:value['created'],size:Math.round((value['size']/1000000+Number.EPSILON)*100)/100,architecture:value['architecture']})}
this.container.innerHTML=Mustache.render(template,{items:items});});}).catch((err)=>{this.container.innerHTML=err});}}