
export{List}
class List{constructor(ctr){this.container=ctr;}
draw(){fetch(active_cmp.constructor.api+'?type=core').then((response)=>{if(response.ok){return response.json()}
throw'Error retrieving services - '+response.status;}).then((data)=>{fetch('/components/system/services/list/template.html').then((response)=>response.text()).then((template)=>{let items=[];for(let[key,value]of Object.entries(data)){items.push({name:key,image:value['image']['name'],hash:value['image']['hash'],status:value['status']})}
this.container.innerHTML=Mustache.render(template,{items:items});});}).catch((err)=>{this.container.innerHTML=err});}}