
const url_params=new URLSearchParams(window.location.search);if(url_params.has('trgt')){document.addEventListener('DOMContentLoaded',(event)=>{let parent_items=document.getElementById("main-nav").getElementsByClassName("uk-parent");let i;for(i=0;i<parent_items.length;i++){let nav_items=parent_items[i].getElementsByTagName("li");let x;for(x=0;x<nav_items.length;x++){if(nav_items[x].firstChild.href.includes(url_params.get('trgt'))){nav_items[x].className+=" uk-active";parent_items[i].className+=" uk-active";return null;}}}});}