
if(window.location.pathname){document.addEventListener('DOMContentLoaded',(event)=>{let nav_items=document.getElementById("main-nav").getElementsByTagName('li');let i;for(i=0;i<nav_items.length;i++){if(window.location.href.includes(nav_items[i].firstElementChild.href)){nav_items[i].className+=" uk-active";}}});}