
if(window.location.pathname){document.addEventListener('DOMContentLoaded',(event)=>{let parent_items=document.getElementById("main-nav").getElementsByClassName("uk-parent");let i;for(i=0;i<parent_items.length;i++){let nav_items=parent_items[i].getElementsByTagName("li");let x;for(x=0;x<nav_items.length;x++){if(window.location.pathname.includes(nav_items[x].firstChild.href)){nav_items[x].className+=" uk-active";parent_items[i].className+=" uk-active";return null;}}}});}