
const url_params=new URLSearchParams(window.location.search);document.addEventListener('DOMContentLoaded',(event)=>{let parent_items=document.getElementById("main-nav").getElementsByClassName("uk-parent");for(let i=0;i<parent_items.length;i++){let nav_items=parent_items[i].getElementsByTagName("li");for(let x=0;x<nav_items.length;x++){if(nav_items[x].firstChild.href.includes(url_params.get('trgt'))){nav_items[x].className+=" uk-active";parent_items[i].className+=" uk-active";}}}})