
function showSpinner(){let spinner=document.createElement('div');spinner.id='task-spinner';spinner.setAttribute('uk-spinner','ratio: 1');spinner.className='uk-flex uk-margin-auto-left uk-padding-small uk-margin-small-right';let container=document.getElementById('main-nav');container.appendChild(spinner);}
function hideSpinner(){let spinner=document.getElementById('task-spinner');spinner.remove();}